import Header from "../components/Header";
import axios from "axios";
import { useEffect, useState } from "react";
// import IMask from "imask";

const Main = ()=>{
    const [state, setState] = useState()
    const [fullName, setFullName] = useState()
    const [date, setDate] = useState()
    const [number, setNumber] = useState()
    const [search, setSearch] = useState()
    console.log(fullName, date, number);

    const addPatient = async()=>{
        const param = {
            "fullName": fullName,
            "date": date,
            "number": number
        }
        const addPatientData = await axios({
            url: `http://localhost:8000/patients`,
            method: "post",
            data: param
        })
        console.log(addPatientData);
        if(addPatientData.status == 201){

            alert('successfully saved!')
        }
    }




    let getPatient = async()=>{
        let getData = await axios({
            method : "get" , 
            url :  `http://localhost:8000/patients`
        })
        if(getData.status == 200) {
            setState(getData.data)
        }
    }
  console.log(state , "state");





    const searchF = async(e)=>{
        // setSearch(e.target.value)
        console.log(search, "hello ggg");

        if(e.target.value != null){
            if(e.target.value.length > 1){
                let fil = state.filter(i => i.fullName.toLowerCase().includes(e.target.value.toLowerCase()))
                console.log(fil);
                setSearch(fil)
            } else{
                setSearch(" This Patient doesn't exist")
            }
        }
    }

//     const phoneInput = document.querySelector("#phone")
//     console.log(phoneInput);

//     const mask = new IMask(phoneInput, {
//     mask: "+996(000)00-00-00"
// })


    useEffect(()=>{
    getPatient()
    } , [])

    return (
        <div className="Main">
            <Header/>

            <div className="main">
                <div className="title-block">
            <h2>Добавить нового пациента</h2>
            <div>
                <p><i className="fa-regular fa-plus"></i><a href="#">Add Content</a></p>
            </div>
                </div>

                <div className="search-form">
                    <input on onChange={(e)=>{searchF(e)}} className="search-form-txt" type="text" name="" placeholder="Search content.."/>
                    <i className="fa-solid fa-magnifying-glass" aria-hidden="true"></i>
                </div>

                {search != null ? <>
                <div className="section">

                    hello
                    <p>{search.fullName}</p> <br />
                    <p>{search.date}</p> <br />
                    <p>{search.number}</p> <br />
                </div> <br />
                </> : <></>}

                <div className="section">
                    <div className="box-style">
                        <h2>Добавить нового пациента</h2>
                        <p class="text-style">Lorem ipsum dolor sit amet consectetur, adipisicing elit. Reiciendis, recusandae!</p>
                    </div> <br/>
                    <div className="box-style">
                    <label for="">ФИО</label>
                    <input onChange={(e)=>setFullName(e.target.value)} className="first-inp" type="text" placeholder="ФИО"/>
                    </div>

                    <div className="inp-block">  
                        <div class="box-style">
                            <label for="">Год рождения</label>
                            <input onChange={(e)=>setDate(e.target.value)} class="second-inp flex" type="text" placeholder="Год рождения"/>
                        </div>
                        <div className="box-style">
                            <label for="">Номер телефона</label>
                        <input onChange={(e)=>setNumber(e.target.value)} className="third-inp flex" id="phone" type="tel" name="phone" placeholder="+996 (000) 00 00 00"/> 
                        </div>
                    </div> <br/>

                    <button onClick={addPatient} className="btn-save">Сохранить</button>
                    <h2>Код: Код с бекенда</h2>
                </div> <br />
            </div>
        </div>
    );
}

export default Main
